export default function galaxy() {
    document.querySelector('.plane.main').addEventListener('mouseenter', function() {
        this.style.animationPlayState = 'paused';
      });
      
      document.querySelector('.plane.main').addEventListener('mouseleave', function() {
        this.style.animationPlayState = 'running';
      });
}
