function generateStars(numberOfStars = 25) {
	for (let i = 0; i < numberOfStars; i++) {
		const star = document.createElement("div");
		star.className = "stars";

		star.style.left = `${Math.random() * 100}%`;
		star.style.top = `${Math.random() * 100}%`;

		star.style.animationDuration = `${Math.random() * 5 + 1}s`; // Duraci�n variable para m�s naturalidad
		star.style.width = `${Math.random() * 2 + 1}px`; // Tama�o variable para diversidad
		star.style.height = star.style.width;
		document.body.appendChild(star);
	}
}

function stopGalaxy() {
	// Selecciona todos los elementos que necesitas
const elements = document.querySelectorAll('.plane.main.circle');

// Itera sobre cada elemento para a�adir el event listener
elements.forEach(function(element) {
    element.addEventListener('mouseenter', function() {
        // A�ade una clase que modifica el estado del pseudo-elemento
        this.classList.add('pause');
    });

    element.addEventListener('mouseleave', function() {
        // Quita la clase para reanudar la animaci�n
        this.classList.remove('pause');
    });
});

}

export { generateStars, stopGalaxy };
